require "spec_helper"

RSpec.describe AdventOfCode::Day1 do
  it 'is a class' do
    expect(described_class).to be_a Class
  end

  describe ".captcha" do
    it "should return 3 when given 1122" do
      expect(AdventOfCode::Day1.captcha("1122")).to eq(3)
    end

    it "should return 4 when given 1111" do
      expect(AdventOfCode::Day1.captcha("1111")).to eq(4)
    end

    it "should return 0 when given 1234" do
      expect(AdventOfCode::Day1.captcha("1234")).to eq(0)
    end

    it "should return 9 when given 91212129" do
      expect(AdventOfCode::Day1.captcha("91212129")).to eq(9)
    end

    it "should give the correct answer for a long number" do
      captcha = ""
      expect(AdventOfCode::Day1.captcha(captcha)).to eq(1228)
    end
  end

  describe ".captcha_two" do
    it "should return 6 when given 1212" do
      expect(AdventOfCode::Day1.captcha_two("1212")).to eq(6)
    end

    it "should return 0 when given 1221" do
      expect(AdventOfCode::Day1.captcha_two("1221")).to eq(0)
    end

    it "should return 4 when given 123425" do
      expect(AdventOfCode::Day1.captcha_two("123425")).to eq(4)
    end

    it "should return 12 when given 123123" do
      expect(AdventOfCode::Day1.captcha_two("123123")).to eq(12)
    end

    it "should return 4 when given 12131415" do
      expect(AdventOfCode::Day1.captcha_two("12131415")).to eq(4)
    end

    it "should solve the test" do
      captcha = ""
      expect(AdventOfCode::Day1.captcha_two(captcha)).to eq(1238)
    end
  end
end
